// hubspot.js

import { useState, useEffect } from 'react';
import {
    Box,
    Button,
    CircularProgress,
    TextField,
    Typography,
    Alert
} from '@mui/material';
import axios from 'axios';

export const HubSpotIntegration = ({ user, org, integrationParams, setIntegrationParams }) => {
    const [isConnected, setIsConnected] = useState(false);
    const [isConnecting, setIsConnecting] = useState(false);
    const [eventForm, setEventForm] = useState({
        objectId: '',
        objectType: 'contact',
        contact_number: '',
        data: ''
    });
    const [isCreatingEvent, setIsCreatingEvent] = useState(false);
    const [eventMessage, setEventMessage] = useState('');
    const [isLoadingObjects, setIsLoadingObjects] = useState(false);
    const [objects, setObjects] = useState([]);
    const [savedTokens, setSavedTokens] = useState(null);
    const [isLoadingTokens, setIsLoadingTokens] = useState(false);

    // Function to open OAuth in a new window
    const handleConnectClick = async () => {
        try {
            setIsConnecting(true);
            const formData = new FormData();
            formData.append('user_id', user);
            formData.append('org_id', org);
            const response = await axios.post(`http://localhost:8000/integrations/hubspot/authorize`, formData);
            const authURL = response?.data;

            const newWindow = window.open(authURL, 'HubSpot Authorization', 'width=600, height=600');

            // Polling for the window to close
            const pollTimer = window.setInterval(() => {
                if (newWindow?.closed !== false) { 
                    window.clearInterval(pollTimer);
                    handleWindowClosed();
                }
            }, 200);
        } catch (e) {
            setIsConnecting(false);
            alert(e?.response?.data?.detail);
        }
    }

    // Function to handle logic when the OAuth window closes
    const handleWindowClosed = async () => {
        try {
            const formData = new FormData();
            formData.append('user_id', user);
            formData.append('org_id', org);
            const response = await axios.post(`http://localhost:8000/integrations/hubspot/credentials`, formData);
            const credentials = response.data; 
            if (credentials) {
                setIsConnecting(false);
                setIsConnected(true);
                setIntegrationParams(prev => ({ ...prev, credentials: credentials, type: 'HubSpot' }));
            }
            setIsConnecting(false);
        } catch (e) {
            setIsConnecting(false);
            alert(e?.response?.data?.detail);
        }
    }

    useEffect(() => {
        setIsConnected(integrationParams?.credentials ? true : false)
    }, [integrationParams?.credentials]);

    // Function to create timeline event
    const handleCreateEvent = async () => {
        try {
            setIsCreatingEvent(true);
            setEventMessage('');
            
            const formData = new FormData();
            formData.append('credentials', JSON.stringify(integrationParams.credentials));
            formData.append('event_data', JSON.stringify(eventForm));
            
            const response = await axios.post(`http://localhost:8000/integrations/hubspot/create-event`, formData);
            
            setEventMessage('Timeline event created successfully!');
            setEventForm({
                objectId: '',
                objectType: 'contact',
                contact_number: '',
                data: ''
            });
        } catch (e) {
            setEventMessage(`Error creating event: ${e?.response?.data?.detail || e.message}`);
        } finally {
            setIsCreatingEvent(false);
        }
    };

    // Function to load existing objects from HubSpot
    const handleLoadObjects = async () => {
        try {
            setIsLoadingObjects(true);
            setEventMessage('');
            
            const formData = new FormData();
            formData.append('credentials', JSON.stringify(integrationParams.credentials));
            
            const response = await axios.post(`http://localhost:8000/integrations/hubspot/load`, formData);
            
            if (response.data && response.data.items) {
                setObjects(response.data.items);
                setEventMessage(`Loaded ${response.data.items.length} objects from HubSpot`);
            }
        } catch (e) {
            setEventMessage(`Error loading objects: ${e?.response?.data?.detail || e.message}`);
        } finally {
            setIsLoadingObjects(false);
        }
    };

    // Function to load saved tokens from JSON file
    const handleLoadSavedTokens = async () => {
        try {
            setIsLoadingTokens(true);
            setEventMessage('');
            
            const response = await axios.get(`http://localhost:8000/integrations/hubspot/tokens/${org}/${user}`);
            
            if (response.data && response.data.tokens) {
                setSavedTokens(response.data.tokens);
                setEventMessage('Saved tokens loaded successfully!');
            }
        } catch (e) {
            setEventMessage(`Error loading saved tokens: ${e?.response?.data?.detail || e.message}`);
        } finally {
            setIsLoadingTokens(false);
        }
    };

    return (
        <>
        <Box sx={{mt: 2}}>
            <Typography variant="h6" gutterBottom>
                HubSpot Integration
            </Typography>
            
            <Box display='flex' alignItems='center' justifyContent='center' sx={{mt: 2}}>
                <Button 
                    variant='contained' 
                    onClick={isConnected ? () => {} : handleConnectClick}
                    color={isConnected ? 'success' : 'primary'}
                    disabled={isConnecting}
                    style={{
                        pointerEvents: isConnected ? 'none' : 'auto',
                        cursor: isConnected ? 'default' : 'pointer',
                        opacity: isConnected ? 1 : undefined
                    }}
                >
                    {isConnected ? 'HubSpot Connected' : isConnecting ? <CircularProgress size={20} /> : 'Connect to HubSpot'}
                </Button>
            </Box>

            {isConnected && (
                <Box sx={{mt: 3}}>
                    <Typography variant="h6" gutterBottom>
                        Create Timeline Event (Template: 6221596)
                    </Typography>
                    
                    {eventMessage && (
                        <Alert severity={eventMessage.includes('Error') ? 'error' : 'success'} sx={{mb: 2}}>
                            {eventMessage}
                        </Alert>
                    )}
                    
                    <Box sx={{mb: 2}}>
                        <Box sx={{display: 'flex', gap: 2, mb: 2}}>
                            <Button
                                variant="outlined"
                                onClick={handleLoadObjects}
                                disabled={isLoadingObjects}
                            >
                                {isLoadingObjects ? <CircularProgress size={20} /> : 'Load Existing Objects'}
                            </Button>
                            
                            <Button
                                variant="outlined"
                                onClick={handleLoadSavedTokens}
                                disabled={isLoadingTokens}
                            >
                                {isLoadingTokens ? <CircularProgress size={20} /> : 'Load Saved Tokens'}
                            </Button>
                        </Box>
                        
                        {savedTokens && (
                            <Box sx={{mt: 2, p: 2, border: '1px solid #ccc', borderRadius: 1, bgcolor: '#f9f9f9'}}>
                                <Typography variant="subtitle2" gutterBottom>
                                    Saved OAuth Tokens:
                                </Typography>
                                <Box sx={{fontFamily: 'monospace', fontSize: '0.8rem'}}>
                                    <div><strong>Access Token:</strong> {savedTokens.access_token?.substring(0, 50)}...</div>
                                    <div><strong>Refresh Token:</strong> {savedTokens.refresh_token?.substring(0, 50)}...</div>
                                    <div><strong>Expires In:</strong> {savedTokens.expires_in} seconds</div>
                                    <div><strong>Token Type:</strong> {savedTokens.token_type}</div>
                                </Box>
                            </Box>
                        )}
                        
                        {objects.length > 0 && (
                            <Box sx={{mt: 2}}>
                                <Typography variant="subtitle2" gutterBottom>
                                    Available Objects (Click to copy ID):
                                </Typography>
                                <Box sx={{display: 'flex', flexWrap: 'wrap', gap: 1}}>
                                    {objects.slice(0, 10).map((obj, index) => (
                                        <Button
                                            key={index}
                                            variant="text"
                                            size="small"
                                            onClick={() => {
                                                setEventForm(prev => ({...prev, objectId: obj.id, objectType: obj.type.toLowerCase()}));
                                                setEventMessage(`Selected ${obj.type}: ${obj.name} (ID: ${obj.id})`);
                                            }}
                                        >
                                            {obj.type}: {obj.name} (ID: {obj.id})
                                        </Button>
                                    ))}
                                </Box>
                            </Box>
                        )}
                    </Box>
                    
                    <Box sx={{display: 'flex', flexDirection: 'column', gap: 2}}>
                        <TextField
                            label="Object ID (Contact/Company/Deal ID)"
                            value={eventForm.objectId}
                            onChange={(e) => setEventForm(prev => ({...prev, objectId: e.target.value}))}
                            placeholder="e.g., 12345"
                            helperText="Enter the numeric ID of an existing contact, company, or deal from HubSpot"
                        />
                        
                        <TextField
                            select
                            label="Object Type"
                            value={eventForm.objectType}
                            onChange={(e) => setEventForm(prev => ({...prev, objectType: e.target.value}))}
                            SelectProps={{native: true}}
                        >
                            <option value="contact">Contact</option>
                            <option value="company">Company</option>
                            <option value="deal">Deal</option>
                        </TextField>
                        
                        <TextField
                            label="Contact Number"
                            value={eventForm.contact_number}
                            onChange={(e) => setEventForm(prev => ({...prev, contact_number: e.target.value}))}
                            placeholder="e.g., +1-555-123-4567"
                        />
                        
                        <TextField
                            label="Data"
                            value={eventForm.data}
                            onChange={(e) => setEventForm(prev => ({...prev, data: e.target.value}))}
                            placeholder="e.g., Additional information or notes"
                            multiline
                            rows={3}
                        />
                        
                        <Button
                            variant="contained"
                            onClick={handleCreateEvent}
                            disabled={isCreatingEvent || !eventForm.objectId || !eventForm.contact_number}
                            sx={{mt: 1}}
                        >
                            {isCreatingEvent ? <CircularProgress size={20} /> : 'Create Timeline Event'}
                        </Button>
                    </Box>
                </Box>
            )}
        </Box>
      </>
    );
}
